package punto1;

public class Principal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 // Declaración de variables
        String nombrePais = "Argentina";
        int edadPersona = 30;
        double alturaEdificio = 120.5; // en metros
        double precioProducto = 200.75; // en la moneda local
        boolean esMayorDeEdad = true;
        char inicialNombre = 'A';
        double resultadoDivision = 8.0 / 6.0;
        double calculo = 0.5;
        
        // Asignar valores a una lista de enteros
        int[] numeros = {8, 6, 2, 5, 1, 3, 9, 7};
        
        // Imprimir las variables
        System.out.println("Nombre del país: " + nombrePais);
        System.out.println("Edad de la persona: " + edadPersona);
        System.out.println("Altura del edificio: " + alturaEdificio + " metros");
        System.out.println("Precio del producto: " + precioProducto);
        System.out.println("¿Es mayor de edad?: " + esMayorDeEdad);
        System.out.println("Inicial del nombre: " + inicialNombre);
        System.out.println("Resultado de la división 8/6: " + resultadoDivision);
        System.out.println("Calculo de 0.5: " + calculo);
        
        // Mostrar los valores del array de números
        System.out.print("Valores en la lista: ");
        for (int numero : numeros) {
            System.out.print(numero + " ");
		

	}

}
	
}
